const sliderVideos=document.querySelectorAll(".slider__video"),sliderDescriptions=document.querySelectorAll(".slider__description");sliderVideos[0].play();for(let e=0;e<4;e++)sliderVideos[e].addEventListener("ended",(()=>{sliderVideos[e].classList.remove("slider__video--current"),sliderDescriptions[e].classList.remove("slider__description--current");const s=e+1===4?0:e+1;sliderVideos[s].classList.add("slider__video--current"),sliderDescriptions[s].classList.add("slider__description--current"),sliderVideos[s].play()}));