const sliderVideos=document.querySelectorAll(".slider__video");if(sliderVideos.length>1){const e=document.querySelectorAll(".slider__description");sliderVideos[0].play();for(let i=0;i<4;i++)sliderVideos[i].addEventListener("ended",(()=>{sliderVideos[i].classList.remove("slider__video--current"),e[i].classList.remove("slider__description--current");const s=i+1===4?0:i+1;sliderVideos[s].classList.add("slider__video--current"),e[s].classList.add("slider__description--current"),sliderVideos[s].play()}))}if(1===sliderVideos.length){const e=document.querySelectorAll(".slider__description");sliderVideos[0].play();let i=0;function activateNextDescription(){const s=i+1===4?0:i+1;e[i].classList.remove("slider__description--current"),e[s].classList.add("slider__description--current"),i=s,setTimeout(activateNextDescription,3e3)}setTimeout(activateNextDescription,3e3)}const mainOffsetTop=document.querySelector(".main").offsetTop,videoWrapper=document.querySelector(".video"),videoBgEl=document.querySelector(".main-header .video__media");window.addEventListener("scroll",(()=>{window.pageYOffset>=mainOffsetTop?(videoBgEl.pause(),videoWrapper.style.display="none"):(videoBgEl.play(),videoWrapper.style.display="")}));