const sliderVideos=document.querySelectorAll(".slider__video");if(sliderVideos.length>0){const e=document.querySelectorAll(".slider__description");sliderVideos[0].play();for(let i=0;i<4;i++)sliderVideos[i].addEventListener("ended",(()=>{sliderVideos[i].classList.remove("slider__video--current"),e[i].classList.remove("slider__description--current");const d=i+1===4?0:i+1;sliderVideos[d].classList.add("slider__video--current"),e[d].classList.add("slider__description--current"),sliderVideos[d].play()}))}const mainOffsetTop=document.querySelector(".main").offsetTop,videoWrapper=document.querySelector(".video"),videoBgEl=document.querySelector(".video__media");window.addEventListener("scroll",(()=>{window.pageYOffset>=mainOffsetTop?(videoBgEl.pause(),videoWrapper.style.position="absolute"):(videoBgEl.play(),videoWrapper.style.position="fixed")}));